// EditCar.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditCar() {
    const { carId } = useParams();
    const [car, setCar] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:25000/data/${carId}`)
            .then(response => response.json())
            .then(data => setCar(data))
            .catch(error => console.error("Erro ao buscar carro:", error));
    }, [carId]);

    const handleSubmit = (e) => {
        e.preventDefault();

        fetch(`http://localhost:25000/data/${carId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(car),
        })
        .then(response => {
            if (response.ok) {
                navigate('/'); // Volta para a lista de carros
            } else {
                console.error("Erro ao editar carro:", response.statusText);
            }
        })
        .catch(error => console.error("Erro ao editar carro:", error));
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setCar(prevCar => ({
            ...prevCar,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    if (!car) return <div>Carregando...</div>;

    return (
        <div>
            <h2>Editar Carro</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Modelo:
                    <input 
                        type="text" 
                        name="modelo" 
                        value={car.modelo} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Marca:
                    <input 
                        type="text" 
                        name="marca" 
                        value={car.marca} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Ano:
                    <input 
                        type="number" 
                        name="ano" 
                        value={car.ano} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Categoria:
                    <input 
                        type="text" 
                        name="categoria" 
                        value={car.categoria} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Disponibilidade:
                    <input 
                        type="checkbox" 
                        name="disponibilidade" 
                        checked={car.disponibilidade} 
                        onChange={handleChange} 
                    />
                </label>
                <button type="submit">Salvar</button>
            </form>
        </div>
    );
}

export default EditCar;
