import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditCar() {
    const { carId } = useParams();
    const [car, setCar] = useState(null);
    const [error, setError] = useState(null); // Estado para capturar erros
    const [success, setSuccess] = useState(null); // Estado para capturar sucesso
    const navigate = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:25000/data/${carId}`)
            .then(response => {
                console.log(response.status); // Verifique o status da resposta
                if (!response.ok) {
                    throw new Error(`Erro: ${response.status} - ${response.statusText}`);
                }
                return response.json();
            })
            .then(data => setCar(data))
            .catch(error => {
                console.error("Erro ao buscar carro:", error);
                setError('Erro ao buscar carro: ' + error.message); // Exibe erro no frontend
            });
    }, [carId]);

    const handleSubmit = (e) => {
        e.preventDefault();

        // Verificar se todos os campos obrigatórios estão preenchidos
        if (!car.modelo || !car.marca || !car.ano || !car.categoria || car.disponibilidade === undefined) {
            setError("Todos os campos são obrigatórios.");
            return;
        }

        console.log("Dados enviados:", car); // Para depuração, confira os dados antes do envio

        fetch(`http://localhost:25000/data/${carId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(car),
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                setError(data.error); // Exibe a mensagem de erro se houver
                setSuccess(null); // Limpa mensagens de sucesso
            } else {
                setSuccess('Carro atualizado com sucesso!');
                setError(null); // Limpa mensagens de erro
                navigate('/'); // Volta para a lista de carros
            }
        })
        .catch(error => {
            setError('Erro ao editar carro: ' + error.message);
            setSuccess(null);
        });
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setCar(prevCar => ({
            ...prevCar,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    if (!car) return <div>Carregando...</div>;

    return (
        <div>
            <h2>Editar Carro</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Modelo:
                    <input 
                        type="text" 
                        name="modelo" 
                        value={car.modelo} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Marca:
                    <input 
                        type="text" 
                        name="marca" 
                        value={car.marca} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Ano:
                    <input 
                        type="number" 
                        name="ano" 
                        value={car.ano} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Categoria:
                    <input 
                        type="text" 
                        name="categoria" 
                        value={car.categoria} 
                        onChange={handleChange} 
                        required 
                    />
                </label>
                <label>
                    Disponibilidade:
                    <input 
                        type="checkbox" 
                        name="disponibilidade" 
                        checked={car.disponibilidade} 
                        onChange={handleChange} 
                    />
                </label>
                <button type="submit">Salvar</button>
            </form>
            {error && <div style={{ color: 'red' }}>{error}</div>}
            {success && <div style={{ color: 'green' }}>{success}</div>}
        </div>
    );
}

export default EditCar;
