import React, { useState } from 'react';

const AddCar = () => {
    const [formData, setFormData] = useState({
        modelo: '',
        marca: '',
        ano: '',
        categoria: '',
        disponibilidade: true, // Campo de disponibilidade, por padrão está true
        image: ''
    });

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value, // Tratamento para checkbox
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Verificar se todos os campos obrigatórios foram preenchidos
        if (!formData.modelo || !formData.marca || !formData.ano || !formData.categoria || !formData.image) {
            alert('Todos os campos devem ser preenchidos.');
            return;
        }

        try {
            const response = await fetch('http://localhost:25000/data', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                alert('Carro adicionado com sucesso!');
            } else {
                alert('Erro ao adicionar carro');
            }
        } catch (error) {
            console.error("Erro ao adicionar carro:", error);
            alert("Erro ao adicionar carro");
        }
    };

    return (
        <div>
            <h1>Adicionar Carro</h1>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    name="modelo" 
                    value={formData.modelo} 
                    onChange={handleChange} 
                    placeholder="Modelo" 
                    required
                />
                <input 
                    type="text" 
                    name="marca" 
                    value={formData.marca} 
                    onChange={handleChange} 
                    placeholder="Marca" 
                    required
                />
                <input 
                    type="number" 
                    name="ano" 
                    value={formData.ano} 
                    onChange={handleChange} 
                    placeholder="Ano" 
                    required
                />
                <input 
                    type="text" 
                    name="categoria" 
                    value={formData.categoria} 
                    onChange={handleChange} 
                    placeholder="Categoria" 
                    required
                />
                <input 
                    type="text" 
                    name="image" 
                    value={formData.image} 
                    onChange={handleChange} 
                    placeholder="URL da Imagem" 
                    required
                />
                <div>
                    <label>
                        Disponível:
                        <input 
                            type="checkbox" 
                            name="disponibilidade" 
                            checked={formData.disponibilidade} 
                            onChange={handleChange}
                        />
                    </label>
                </div>
                <button type="submit">Adicionar</button>
            </form>
        </div>
    );
};

export default AddCar;
