// App.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';
import AddCar from './AddCar';
import EditCar from './EditCar';

function App() {
    const [data, setData] = useState([]);
    const [selectedCarId, setSelectedCarId] = useState(null); // Estado para armazenar o ID do carro selecionado
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        fetch('http://localhost:25000/data')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error("Erro ao buscar os dados:", error));
    };

    const handleAdd = () => {
        navigate('/add'); // Navega para a página de adicionar carro
    };

    const handleEdit = (carId) => {
        navigate(`/edit/${carId}`); // Navega para a página de editar carro
    };

    const handleDelete = (carId) => {
        if (window.confirm("Tem certeza que deseja excluir este carro?")) {
            fetch(`http://localhost:25000/data/${carId}`, {
                method: 'DELETE',
            })
            .then(response => {
                if (response.ok) {
                    fetchData(); // Recarregar os dados após exclusão
                } else {
                    console.error("Erro ao excluir o carro:", response.statusText);
                }
            })
            .catch(error => console.error("Erro ao excluir o carro:", error));
        }
    };

    return (
        <div className="App">
            <h1>Car List</h1>
            <button onClick={handleAdd}>Adicionar Carro</button>
            <button onClick={() => selectedCarId ? handleEdit(selectedCarId) : alert("Selecione um carro para editar.")}>
                Editar Carro
            </button>
            <button onClick={() => selectedCarId ? handleDelete(selectedCarId) : alert("Selecione um carro para excluir.")}>
                Excluir Carro
            </button>
            <table>
                <thead>
                    <tr>
                        <th>Modelo</th>
                        <th>Marca</th>
                        <th>Ano</th>
                        <th>Categoria</th>
                        <th>Disponibilidade</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {Array.isArray(data) && data.map(car => (
                        <tr key={car.id} onClick={() => setSelectedCarId(car.id)}> {/* Adiciona a seleção do carro */}
                            <td>{car.modelo}</td>
                            <td>{car.marca}</td>
                            <td>{car.ano}</td>
                            <td>{car.categoria}</td>
                            <td>{car.disponibilidade ? 'Disponível' : 'Indisponível'}</td>
                            <td>
                                <button onClick={() => handleEdit(car.id)}>Editar</button> {/* Botão de editar dentro da tabela */}
                                <button onClick={() => handleDelete(car.id)}>Excluir</button> {/* Botão de excluir dentro da tabela */}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;
