// EditCar.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function EditCar() {
    const { id } = useParams();
    const [editCar, setEditCar] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:25000/data/${id}`)
            .then(response => response.json())
            .then(data => setEditCar(data))
            .catch(error => console.error("Erro ao buscar o carro:", error));
    }, [id]);

    const handleUpdateCar = () => {
        fetch(`http://localhost:25000/data/${id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(editCar)
        })
        .then(() => history.push('/'))
        .catch(error => console.error("Erro ao atualizar o carro:", error));
    };

    if (!editCar) return <div>Loading...</div>;

    return (
        <div>
            <h2>Editar Carro</h2>
            <input type="text" value={editCar.modelo} onChange={e => setEditCar({ ...editCar, modelo: e.target.value })} />
            <input type="text" value={editCar.marca} onChange={e => setEditCar({ ...editCar, marca: e.target.value })} />
            <input type="number" value={editCar.ano} onChange={e => setEditCar({ ...editCar, ano: e.target.value })} />
            <input type="text" value={editCar.categoria} onChange={e => setEditCar({ ...editCar, categoria: e.target.value })} />
            <label>
                Disponibilidade:
                <input type="checkbox" checked={editCar.disponibilidade} onChange={e => setEditCar({ ...editCar, disponibilidade: e.target.checked })} />
            </label>
            <button onClick={handleUpdateCar}>Atualizar</button>
        </div>
    );
}

export default EditCar;
